<?php

require_once(__DIR__ . '/../../vendor/autoload.php');

use PHPUnit\Framework\TestCase;

use sgoendoer\Sonic\Sonic;
use sgoendoer\Sonic\Identity\GID;
use sgoendoer\Sonic\Identity\UOID;
use sgoendoer\Sonic\Identity\EntityAuthData;
use sgoendoer\Sonic\Identity\SocialRecord;
use sgoendoer\Sonic\Identity\SocialRecordBuilder;
use sgoendoer\Sonic\Identity\SocialRecordManager;

class IdentityUnitTest extends TestCase
{
	public $platformSRjson = '{"socialRecord":{"@context":"http://sonic-project.net/","@type":"socialrecord","type":"platform","globalID":"2UZCAI2GM45T160MDN44OIQ8GKN5GGCKO96LC9ZOQCAEVAURA8","platformGID":"2UZCAI2GM45T160MDN44OIQ8GKN5GGCKO96LC9ZOQCAEVAURA8","displayName":"social.snet.tu-berlin.de","profileLocation":"http://social.snet.tu-berlin.de/sonic-sdk/","personalPublicKey":"-----BEGIN PUBLIC KEY----------END PUBLIC KEY-----","accountPublicKey":"-----BEGIN PUBLIC KEY----------END PUBLIC KEY-----","salt":"82daf2b77b46f36d","datetime":"2016-01-13T10:58:52+01:00","active":1,"keyRevocationList":[]},"accountPrivateKey":"-----BEGIN PRIVATE KEY----------END PRIVATE KEY-----","personalPrivateKey":"-----BEGIN PRIVATE KEY----------END PRIVATE KEY-----"}';
	
	public $aliceSRjson = '{"socialRecord":{"@context":"http://sonic-project.net/","@type":"socialrecord","type":"user","globalID":"4802C8DE6UZZ5BICQI830A8P8BW3YB5EBPGXWNRH1EP7H838V7","platformGID":"2UZCAI2GM45T160MDN44OIQ8GKN5GGCKO96LC9ZOQCAEVAURA8","displayName":"Alice","profileLocation":"http://social.snet.tu-berlin.de/sonic-sdk/","personalPublicKey":"-----BEGIN PUBLIC KEY----------END PUBLIC KEY-----","accountPublicKey":"-----BEGIN PUBLIC KEY----------END PUBLIC KEY-----","salt":"abb0afd289f102f3","datetime":"2016-01-13T10:58:54+01:00","active":1,"keyRevocationList":[]},"accountPrivateKey":"-----BEGIN PRIVATE KEY----------END PRIVATE KEY-----","personalPrivateKey":"-----BEGIN PRIVATE KEY----------END PRIVATE KEY-----"}';
	
	public $sonic = NULL;
	
	public $platformSR = NULL;
	public $platformSocialRecord = NULL;
	public $platformAccountKeyPair = NULL;
	public $platformPersonalKeyPair = NULL;
	
	public $aliceSR = NULL;
	public $aliceSocialRecord = NULL;
	public $aliceAccountKeyPair = NULL;
	public $alicePersonalKeyPair = NULL;
	
	public function __construct()
	{
		$this->platformSR = SocialRecordManager::importSocialRecord($this->platformSRjson);
		$this->platformSocialRecord = $this->platformSR['socialRecord'];
		$this->platformAccountKeyPair = $this->platformSR['accountKeyPair'];
		$this->platformPersonalKeyPair = $this->platformSR['personalKeyPair'];
		
		$this->aliceSR = SocialRecordManager::importSocialRecord($this->aliceSRjson);
		$this->aliceSocialRecord = $this->aliceSR['socialRecord'];
		$this->aliceAccountKeyPair = $this->aliceSR['accountKeyPair'];
		$this->alicePersonalKeyPair = $this->aliceSR['personalKeyPair'];
		
		$this->sonic = Sonic::initInstance(new EntityAuthData(
										$this->platformSocialRecord,
										$this->platformAccountKeyPair,
										$this->platformPersonalKeyPair));
		
		Sonic::setUserAuthData(new EntityAuthData($this->aliceSocialRecord, $this->aliceAccountKeyPair));
		Sonic::setContext(Sonic::CONTEXT_USER);
	}
	
	public function testGID()
	{
		$key = "-----BEGIN PUBLIC KEY----------END PUBLIC KEY-----";
		
		$salt = "0b9357af0b74e6a4";
		
		$gid1 = '3Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0SKYUKKGVT8QWD78T5';
		$gid2 = '2Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0SKYUKKGVT8QWD78T5';
		$gid3 = '2Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0';
		$gid4 = '2-Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0SKYUKKGVT8QWD78T';
		
		$this->assertEquals($gid1, GID::createGID($key, $salt));
		$this->assertEquals(true, GID::isValid($gid1));
		$this->assertEquals(true, GID::verifyGID($key, $salt, $gid1));
		
		$this->assertNotEquals($gid2, GID::createGID($key, $salt));
		$this->assertTrue(GID::isValid($gid2));
		$this->assertFalse(GID::verifyGID($key, $salt, $gid2));
		
		$this->assertFalse(GID::isValid($gid3));
		$this->assertFalse(GID::isValid($gid4));
	}
	
	public function testUOID()
	{
		$uoid1 = '3Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0SKYUKKGVT8QWD78T5:0b9357af0b74e6a4'; // ok
		$uoid2 = '2Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0:0b9357af0b74e6a4'; // invalid GID
		$uoid3 = '2-Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0SKYUKKGVT8QWD78T:0b9357af0b74e6a4'; // invalid GID
		$uoid4 = '3Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0SKYUKKGVT8QWD78T5:0b94357af0b74e6a4'; // invalid id
		$uoid5 = '3Z51OUSP4UPXTEOOKSDXVZQLMYGNJQVY0SKYUKKGVT8QWD78T5:0b94357_0b74e6a4'; // invalid id
		
		$this->assertTrue(UOID::isValid($uoid1));
		$this->assertFalse(UOID::isValid($uoid2));
		$this->assertFalse(UOID::isValid($uoid3));
		$this->assertFalse(UOID::isValid($uoid4));
		$this->assertFalse(UOID::isValid($uoid5));
	}
	
	public function testSocialRecord()
	{
		$socialRecord = (new SocialRecordBuilder())
			->type($this->aliceSocialRecord->getType())
			->globalID($this->aliceSocialRecord->getGlobalID())
			->platformGID($this->aliceSocialRecord->getPlatformGID())
			->salt($this->aliceSocialRecord->getSalt())
			->accountPublicKey($this->aliceSocialRecord->getAccountPublicKey())
			->personalPublicKey($this->aliceSocialRecord->getPersonalPublicKey())
			->displayName($this->aliceSocialRecord->getDisplayName())
			->profileLocation($this->aliceSocialRecord->getProfileLocation())
			->datetime($this->aliceSocialRecord->getDatetime())
			->active($this->aliceSocialRecord->getActive())
			->keyRevocationList($this->aliceSocialRecord->getKeyRevocationList())
			->build();
		
		$srString = $this->aliceSocialRecord->getJSONString();
		
		$this->assertEquals($srString, $socialRecord->getJSONString());
	}
}

?>