					->members(array('28B6TE8T9NUO202C5NZIUTNQSP88E70B8JAWH4FQ58OJOB8LIF'))
					->build();
					
		$this->assertTrue($group->validate());
		$this->assertEquals($group, AccessControlGroupObjectBuilder::buildFromJSON($group->getJSONString()));
	}
	
